import React, { Component } from 'react'
import ReactApexChart from 'react-apexcharts'


class Chart extends Component {
    
      state = {
        time: [1, 2 ,3],
        series: [{
            name: 'sales',
            data: [30,40,35,50,49,60,70,91,125]
        }],
        options: {
          chart: {
            id: 'realtime',
            height: 350,
            type: 'line',
            animations: {
              enabled: true,
              easing: 'linear',
              dynamicAnimation: {
                speed: 1000
              }
            },
            toolbar: {
              show: false
            },
            zoom: {
              enabled: false
            }
          },
          dataLabels: {
            enabled: false
          },
          stroke: {
            curve: 'smooth'
          },
          title: {
            text: 'Dynamic Updating Chart',
            align: 'left'
          },
          markers: {
            size: 0
          },
          xaxis: {
            type: 'datetime',
            categories: [1991,1992,1993,1994,1995,1996,1997, 1998,1999]
            // range: XAXISRANGE,
          },
          yaxis: {
            max: 100
          },
          legend: {
            show: false
          },
        }, 
      };
  


    updateData(prevState) {
        // console.log(prevState)
        // const data = Math.floor(Math.random() * 101)
        // const prevData = prevState.series[0].data
        // const newData = prevData.push(data)
        // console.log("[Data] " + data)
        // console.log("[PrevData] " + prevData)
        // console.log("[NewData] " + newData)
        // return prevData
        // const newSeries = [];

        // this.state.series.map((s) => {
        // const data = s.data.map(() => {
        //     return Math.floor(Math.random() * 101)
        // })
        // newSeries.push({ data, name: s.name })
        // })
    }

    componentDidMount() {
        // this.interval = setInterval(() => this.setState(prevState => ({
        //     series: [{
        //      name:this.state.series[0].name,
        //      data: this.updateData(prevState)
        //     }]
        //   })), 1000);
    }

    updateCharts() {
        const max = 90;
        const min = 30;
        const newSeries = [];
        console.log(this)

    
        this.state.series.forEach((s) => {
          const data = s.data.map(() => {
            return Math.floor(Math.random() * (180 - min + 1)) + min;
          });
          newSeries.push({ data, name: s.name });
        });
    
        this.setState({
          series: newSeries,
        });
      }

    componentWillUnmount() {
        clearInterval(this.interval);
      }
  
    render() {
        console.log(this)
        return (
            <div id="chart">  
                <ReactApexChart options={this.state.options} series={this.state.series} type="line" height={350} />
                <button onClick={this.updateCharts}> Click </button>
            </div>
        )
    }
}
 
export default Chart
