import React, { Component } from 'react'
import ReactApexChart from 'react-apexcharts'


class DotaChart extends Component {
    
  state = {
    // series: [],
    series: [
      {
        name: 'Dota',
        data: [1, 2, 3],
        url: "https://api.twitch.tv/helix/streams/?first=100&game_id=29595",
        dataType: "live-chart"
      },
      {
        name: 'RocketLeague',
        data: [12, 53, 1],
        url: "https://api.twitch.tv/helix/streams/?first=100&game_id=30921",
        dataType: "live-chart"
      },
      {
        name: 'Hearthstone',
        data: [23, 14, 23],
        url: "https://api.twitch.tv/helix/streams/?first=100&game_id=138585",
        dataType: "live-chart"
      },
  ],
    options: {
      chart: {
        id: 'realtime',
        height: 350,
        type: 'line',
        animations: {
          enabled: true,
          easing: 'linear',
          dynamicAnimation: {
            speed: 1000
          }
        },
        toolbar: {
          show: false
        },
        zoom: {
          enabled: false
        }
      },
      dataLabels: {
        enabled: false
      },
      stroke: {
        curve: 'smooth'
      },
      title: {
        text: 'Viewer count of Dota 2, Rocket League and HearthStone',
        align: 'left'
      },
      markers: {
        size: 0
      },
      xaxis: {
        // type: 'datetime',
        // categories: [1991,1992,1993,1994,1995,1996,1997, 1998,1999],
        // range: 10,
      },
      yaxis: {
        // max: 100
      },
      legend: {
        show: false
      },
    }, 
  };

  componentDidUpdate = (previousProps) => {
    // if (previousProps !== this.props) {
    //   this.setState({
    //     series: this.props.series
    //   })
    // }
  }
    
  render() {
      return (
          <div id="chart">  
              <ReactApexChart style={{width: "80%"}} options={this.state.options} series={this.state.series} type="line" height={350} />
          </div>
      )
  }
}
 
export default DotaChart
