// export default DotaChart

import React, { Component } from 'react'
import ReactApexChart from 'react-apexcharts'
import axios from "axios"
import api from '../../api'


class DotaChart extends Component {
    
      state = {
        series: [
          {
            name: 'Dota',
            data: [10],
            url: "https://api.twitch.tv/helix/streams/?first=100&game_id=29595",
            type: "chart"
          },
          {
            name: 'RocketLeague',
            data: [3],
            url: "https://api.twitch.tv/helix/streams/?first=100&game_id=30921",
            type: "chart"
          },
          {
            name: 'Hearthstone',
            data: [1],
            url: "https://api.twitch.tv/helix/streams/?first=100&game_id=138585",
            type: "chart"
          },
        ],
        options: {
          chart: {
            id: 'realtime',
            height: 350,
            type: 'line',
            animations: {
              enabled: true,
              easing: 'linear',
              dynamicAnimation: {
                speed: 1000
              }
            },
            toolbar: {
              show: false
            },
            zoom: {
              enabled: false
            }
          },
          dataLabels: {
            enabled: false
          },
          stroke: {
            curve: 'smooth'
          },
          title: {
            text: 'Dynamic Updating Chart',
            align: 'left'
          },
          markers: {
            size: 0
          },
          xaxis: {
            // type: 'datetime',
            // categories: [1991,1992,1993,1994,1995,1996,1997, 1998,1999],
            // range: 10,
          },
          yaxis: {
            // max: 100
          },
          legend: {
            show: false
          },
        }, 
      };
   
    render() {
        return (
            <div id="chart">  
                <ReactApexChart options={this.state.options} series={this.state.series} type="line" height={350} />
            </div>
        )
    }
}
 
export default DotaChart
