import React, { Component } from 'react'
import ReactApexChart from 'react-apexcharts'


class Chart extends Component {
    constructor(props) {
      super(props);
      this.state = {
        series: [{
            name: 'sales',
            data: [30,40,35,50,49,60,70,91,125]
        }],
        options: {
          chart: {
            id: 'realtime',
            height: 350,
            type: 'line',
            animations: {
              enabled: true,
              easing: 'linear',
              dynamicAnimation: {
                speed: 1000
              }
            },
            toolbar: {
              show: false
            },
            zoom: {
              enabled: false
            }
          },
          dataLabels: {
            enabled: false
          },
          stroke: {
            curve: 'smooth'
          },
          title: {
            text: 'Dynamic Updating Chart',
            align: 'left'
          },
          markers: {
            size: 0
          },
          xaxis: {
            type: 'datetime',
            categories: [1991,1992,1993,1994,1995,1996,1997, 1998,1999]
            // range: XAXISRANGE,
          },
          yaxis: {
            max: 100
          },
          legend: {
            show: false
          },
        }, 
      };
    }
  
    // componentDidMount() {
    //     setInterval(() => this.forceUpdate(), 1000);
    // }

    // componentDidUpdate() {
    //     this.updateData()
    // }

    // componentDidMount() {
    //     window.setInterval(() => {
    //     //   getNewSeries(lastDate, {
    //     //     min: 10,
    //     //     max: 90
    //     //   })
          
    //       ApexCharts.exec('realtime', 'updateSeries', [{
    //         data: Math.floor(Math.random() * 101)
    //       }])
    //     }, 1000)
    //   }

    updateData() {
        let data = Math.floor(Math.random() * 101)
        this.setState({
            series: [{
                data: data
            }]
        })

    }
  
    render() {

        return (
            <div id="chart">  
                <ReactApexChart options={this.state.options} series={this.state.series} type="line" height={350} />
            </div>
        )
    }
}
 
export default Chart
