import React, { Component } from 'react';
import './App.scss';
import ChessChart from "./containers/Charts/ChessChart"
import DotaChart from "./containers/Charts/DotaChart"

class  App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      series: []
    }
    this.webSocket = new WebSocket("ws://localhost:3001")
  }

  componentDidMount() {
    this.webSocket.addEventListener('open', function(event) {
      console.log('Client connected');
    });
    this.webSocket.addEventListener('error', function(error) {
      console.log('Error : ', error);
    });
    console.log("componentDidMount <App />");
    this.webSocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      this.setState({
        series: data
      })
    }
  }

  render() {

    const chessData = this.state.series.series.filter(serie => serie.name === "Chess")

    return (
      <div className="App">
        <ChessChart data={chessData} />
        {/* <DotaChart data={this.state.series} /> */}
      </div>
    );
  }
}

export default App;
