import React, { Component } from 'react'
import ReactApexChart from 'react-apexcharts'


class Chart extends Component {
    
      state = {
        series: [{
            name: 'sales',
            data: [30]
        }],
        options: {
          chart: {
            id: 'realtime',
            height: 350,
            type: 'line',
            animations: {
              enabled: true,
              easing: 'linear',
              dynamicAnimation: {
                speed: 1000
              }
            },
            toolbar: {
              show: false
            },
            zoom: {
              enabled: false
            }
          },
          dataLabels: {
            enabled: false
          },
          stroke: {
            curve: 'smooth'
          },
          title: {
            text: 'Dynamic Updating Chart',
            align: 'left'
          },
          markers: {
            size: 0
          },
          xaxis: {
            type: 'datetime',
            categories: [1991,1992],
            range: 10,
          },
          yaxis: {
            max: 100
          },
          legend: {
            show: false
          },
        }, 
      };
  


    updateData = () => {

        const oldSeries = [...this.state.series[0].data]
        const newSeries = oldSeries
        const data = Math.floor(Math.random() * 100)
        newSeries.push(data);
        
        this.setState({
          series: [{
            data: newSeries,
            name: "sales"
          }]
        });
        console.log(this.state)
    }

    componentDidMount() {
        this.interval = setInterval(() => this.updateData(), 1000);
    }

    componentWillUnmount() {
        clearInterval(this.interval);
    }
  
    render() {
        return (
            <div id="chart">  
                <ReactApexChart options={this.state.options} series={this.state.series} type="line" height={350} />
            </div>
        )
    }
}
 
export default Chart
