import React, { Component } from 'react'
import ReactApexChart from 'react-apexcharts'
import axios from "axios"
import api from '../../api'


class ChessChart extends Component {
    
      state = {
        totalViewers: 0,
        series: [
          {
            name: 'Chess Viewers',
            data: []
          },
        ],
        options: {
          chart: {
            id: 'realtime',
            height: 350,
            type: 'line',
            animations: {
              enabled: true,
              easing: 'linear',
              dynamicAnimation: {
                speed: 1000
              }
            },
            toolbar: {
              show: false
            },
            zoom: {
              enabled: false
            }
          },
          dataLabels: {
            enabled: false
          },
          stroke: {
            curve: 'smooth'
          },
          title: {
            text: 'Dynamic Updating Chart',
            align: 'left'
          },
          markers: {
            size: 0
          },
          xaxis: {
            // type: 'datetime',
            // categories: [1991,1992,1993,1994,1995,1996,1997, 1998,1999],
            // range: 10,
          },
          yaxis: {
            // max: 100
          },
          legend: {
            show: false
          },
        }, 
      };
      
      
    componentDidMount() {
      // this.interval = setInterval(() => this.fetchData(), 1000)
    }
    
    componentWillUnmount() {
        clearInterval(this.interval);
    }

    updateData = (allViewers) => {
      const oldSeriesData = [...this.state.series[0].data]
      const newSeriesData = oldSeriesData
      newSeriesData.push(allViewers)
      
      this.setState({
        totalViewers: allViewers,
        series: [{
          data: newSeriesData,
          name: "Chess Viewers"
        }]
      });
    }

    sumViewers = (data) => {
      
      let totalViewers = 0
      for (let stream of data.data) {
        totalViewers += stream.viewer_count
      }
      return totalViewers
    }

    fetchData = async () => {
      const getToken = await axios.post(`https://id.twitch.tv/oauth2/token?client_id=${process.env.REACT_APP_TWITCH_API_CLIENT_ID}&client_secret=${process.env.REACT_APP_TWITCH_API_CLIENT_SECRET}&grant_type=client_credentials&scope=analytics:read:games`)
        .then(response => response)
        .catch(e => console.log(e))

      const chessData = await fetch(
        "https://api.twitch.tv/helix/streams/?first=100&game_id=743",
        {
          "headers": {
            "Client-ID": process.env.REACT_APP_TWITCH_API_CLIENT_ID,
            "Authorization": "Bearer " + getToken.data.access_token
        }
        }
      )
      .then(response => response.json())
      .catch(e => console.log(e));

      const allViewers = this.sumViewers(chessData)
      this.updateData(allViewers)
    }
  
    render() {
        return (
            <div id="chart">  
                <ReactApexChart options={this.state.options} series={this.state.series} type="line" height={350} />
                <div> Total Viewers : {this.state.totalViewers} </div>
            </div>
        )
    }
}
 
export default ChessChart
