import React, { Component } from 'react'
import ReactApexChart from 'react-apexcharts'
import axios from "axios"
import api from '../../api'


class Chart extends Component {
    
      state = {
        series: [
          {
            name: 'Dota',
            data: []
          },
          {
            name: 'RocketLeague',
            data: [2]
          },
          {
            name: 'Hearthstone',
            data: [3]
          },
        ],
        options: {
          chart: {
            id: 'realtime',
            height: 350,
            type: 'line',
            animations: {
              enabled: true,
              easing: 'linear',
              dynamicAnimation: {
                speed: 1000
              }
            },
            toolbar: {
              show: false
            },
            zoom: {
              enabled: false
            }
          },
          dataLabels: {
            enabled: false
          },
          stroke: {
            curve: 'smooth'
          },
          title: {
            text: 'Dynamic Updating Chart',
            align: 'left'
          },
          markers: {
            size: 0
          },
          xaxis: {
            // type: 'datetime',
            // categories: [1991,1992,1993,1994,1995,1996,1997, 1998,1999],
            // range: 10,
          },
          yaxis: {
            // max: 100
          },
          legend: {
            show: false
          },
        }, 
      };
  


    updateData = (allDotaViewers, allRocketLeagueViewers, allHearthstoneViewers) => {

        let dotaViewers
        let rocketLeagueViewers
        let hearthstoneViewers

        for (let serie of this.state.series) {
            if (serie.name === "Dota") {
                dotaViewers = this.updateSeriesData(serie.data, allDotaViewers)
                this.setState(prevState => ({
                    series: prevState.series.map(
                    obj => (obj.name === "Dota" ? Object.assign(obj, { data: dotaViewers }) : obj)
                  )
                }));
            }
            if (serie.name === "RocketLeague") {
                rocketLeagueViewers = this.updateSeriesData(serie.data, allRocketLeagueViewers)
            }
            if (serie.name === "Hearthstone") {
                hearthstoneViewers = this.updateSeriesData(serie.data, allHearthstoneViewers)
            }            
      }

        

        console.log(this.state)
      
    }

    updateSeriesData = (prevViewers, updatedViewers) => {
        const oldSeriesData = [...prevViewers]
        const newSeriesData = oldSeriesData
        newSeriesData.push(updatedViewers)
        return newSeriesData
    }

    componentDidMount() {
    //   this.interval = setInterval(() => this.fetchData(), 1000)
         this.fetchData()
    }

    sumViewers = (data) => {
      
      let totalViewers = 0
      for (let stream of data.data) {
        totalViewers += stream.viewer_count
      }
      return totalViewers
    }

    fetchData = async () => {
      const getToken = await axios.post("https://id.twitch.tv/oauth2/token?client_id=kyeb25g56k9enf1h06t5pbhfzr08ed&client_secret=i69ag27ehweltcib9303nsai03cytr&grant_type=client_credentials&scope=analytics:read:games")
        .then(response => response)
        .catch(e => console.log(e))

      const dotaData = await fetch(
        "https://api.twitch.tv/helix/streams/?first=100&game_id=29595",
        {
          "headers": {
            "Client-ID": "kyeb25g56k9enf1h06t5pbhfzr08ed",
            "Authorization": "Bearer " + getToken.data.access_token
        }
        }
      )
      .then(response => response.json())
      .catch(e => console.log(e));

      const rocketLeagueData = await fetch(
        "https://api.twitch.tv/helix/streams/?first=100&game_id=30921",
        {
          "headers": {
            "Client-ID": "kyeb25g56k9enf1h06t5pbhfzr08ed",
            "Authorization": "Bearer " + getToken.data.access_token
        }
        }
      )
      .then(response => response.json())
      .catch(e => console.log(e));

      const hearthstoneData = await fetch(
        "https://api.twitch.tv/helix/streams/?first=100&game_id=138585",
        {
          "headers": {
            "Client-ID": "kyeb25g56k9enf1h06t5pbhfzr08ed",
            "Authorization": "Bearer " + getToken.data.access_token
        }
        }
      )
      .then(response => response.json())
      .catch(e => console.log(e));


      const allDotaViewers = this.sumViewers(dotaData)
      const allRocketLeagueViewers = this.sumViewers(rocketLeagueData)
      const allHearthstoneViewers = this.sumViewers(hearthstoneData)
      this.updateData(allDotaViewers, allRocketLeagueViewers, allHearthstoneViewers)
    }

    componentWillUnmount() {
        clearInterval(this.interval);
    }
  
    render() {
        return (
            <div id="chart">  
                <ReactApexChart options={this.state.options} series={this.state.series} type="line" height={350} />
            </div>
        )
    }
}
 
export default Chart

