import React, { Component } from 'react'
import ReactApexChart from 'react-apexcharts'


class Charts extends Component {
    
  state = {
    series: [],
    options: {
      chart: {
        id: 'realtime',
        height: 600,
        type: 'line',
        toolbar: {
          show: false
        },
        zoom: {
          enabled: false
        }
      },
      stroke: {
        curve: 'smooth'
      },
      title: {
        text: 'Viewer count of Dota 2, Rocket League and HearthStone',
        align: 'center'
      },
      markers: {
        size: 0
      },
      xaxis: {
        type: 'category',
        min: 0,
        range: 10,
        title: {
          text: "Time"
        }
      },
      yaxis: {
        title: {
          text: "Viewers"
        }
      },
      legend: {
        show: true
      },
    }, 
  };
    
  render() {
      return (
          <div id="chart">  
              <ReactApexChart style={{width: "80%"}} options={this.state.options} series={this.props.series} type="line" height={500} />
          </div>
      )
  }
}
 
export default Charts
