import React, { Component } from 'react';
import './App.scss';
import ChessChart from "./containers/Charts/ChessChart"
import DotaChart from "./containers/Charts/DotaChart"

class  App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      chessViewers: 0,
      series: []
    }
    this.webSocket = new WebSocket("ws://localhost:3001")
  }

  componentDidMount() {
    this.webSocket.addEventListener('open', function(event) {
      console.log('Client connected');
    });
    this.webSocket.addEventListener('error', function(error) {
      console.log('Error : ', error);
    });
    console.log("componentDidMount <App />");
    this.webSocket.onmessage = (event) => {
      const data = JSON.parse(event.data)
      const [chessViewers] = data.series.filter(serie => serie.name === "Chess")
      const series = data.series.filter(serie => serie.type === "chart")
        this.setState({
          chessViewers: chessViewers.data,
          series: series
        })
    }
  }

  render() {

    return (
      <div className="App">
        <ChessChart chessViewers={this.state.chessViewers} />
        <DotaChart data={this.state.series} />
      </div>
    );
  }
}

export default App;
